mapper.update:
UPDATE BOARD
SET    BOARD_TITLE     = #{boardTitle},
       BOARD_CONTENT   = #{boardContent},
       BOARD_ST        = #{boardSt},
       CAT_CONTENT	   = #{catContent}
WHERE  BOARD_SQ        = #{boardSq}

UPDATE BOARD
SET    BOARD_ST        = 'N'
WHERE  BOARD_SQ        = #{boardSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT NVL(BOARD_SQ, 0) BOARD_SQ, NVL(BOARD_GB_SQ, 0) BOARD_GB_SQ, LEVEL, LPAD(' ',(LEVEL-1)*4 )|| BOARD_TITLE BOARD_TITLE, BOARD_CONTENT, TO_CHAR(BOARD_DATE, 'YYYY-MM-DD') BOARD_DATE, BOARD_ST, NVL(GROUP_NO, 0) GROUP_NO, NVL(PARENT_SQ, 0) PARENT_SQ, MEM_ID, CAT_CONTENT FROM BOARD WHERE BOARD_GB_SQ = #{boardGbSq}
AND BOARD_TITLE LIKE '%' || #{searchKeyword} || '%'
AND BOARD_CONTENT LIKE '%' || #{searchKeyword} || '%'
START WITH PARENT_SQ = 0 CONNECT BY PRIOR BOARD_SQ = PARENT_SQ ORDER SIBLINGS BY GROUP_NO DESC, BOARD_SQ ASC ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

SELECT count(*) totCnt FROM BOARD WHERE BOARD_GB_SQ = #{boardGbSq}
AND BOARD_TITLE LIKE '%' || #{searchKeyword} || '%'
AND BOARD_CONTENT LIKE '%' || #{searchKeyword} || '%'
