mapper.update:
UPDATE BOARD_GB
SET    BOARD_GB_NM  = #{boardGbNm},
	   BOARD_GB_ST  = #{boardGbSt}
WHERE  BOARD_GB_SQ  = #{boardGbSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT BOARD_GB_SQ, BOARD_GB_NM, BOARD_GB_ST FROM BOARD_GB
AND BOARD_GB_NM LIKE '%' || #{searchKeyword} || '%'
ORDER BY BOARD_GB_SQ DESC ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
