mapper.update:
UPDATE CATEGORY
SET    CAT_CONTENT  = #{catContent},
	   CAT_ST  = #{catSt}
WHERE  CAT_SQ  = #{catSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT * FROM CATEGORY WHERE B.BOARD_GB_SQ = TO_NUMBER(#{boardGbSq})
AND CAT_CONTENT '%' || #{searchKeyword} || '%'
ORDER BY CAT_SQ DESC ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
