mapper.select:
SELECT habit_gb
FROM habit
WHERE habit_sq = #{habitSq}

mapper.update:
UPDATE habit 
   SET habit_gb = #{habitGb},
 	   habit_st = #{habitSt}
 WHERE habit_sq = #{habitSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT habit_sq, habit_gb, habit_st FROM habit WHERE 1=1
AND habit_gb LIKE '%' || #{searchKeyword} || '%'
ORDER BY habit_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
