mapper.update:
UPDATE INTERVIEW
SET    INTERVIEW_NM  = #{interviewNm}
WHERE  INTERVIEW_SQ  = #{interviewSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT NVL(INTERVIEW_SQ, 0) INTERVIEW_SQ, INTERVIEW_NM, TO_CHAR(INTERVIEW_DATE, 'YYYY-MM-DD') INTERVIEW_DATE, MEM_ID FROM INTERVIEW WHERE 1=1
AND MEM_ID = #{memId}
AND INTERVIEW_NM LIKE '%' || #{searchKeyword} || '%'
AND MEM_ID LIKE '%' || #{searchKeyword} || '%'
) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
