mapper.update:
UPDATE keyword 
   SET keyword_content = #{keywordContent}
 WHERE keyword = #{keywordSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT keyword_sq, keyword_content FROM keyword WHERE 1=1
AND keyword_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY keyword_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT keyword_sq, keyword_content FROM keyword WHERE keyword_sq IN ( SELECT keyword_sq FROM keyword_matching WHERE talent_sq = #{talentSq})
AND keyword_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY keyword_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

SELECT count(*)
  FROM keyword
 WHERE keyword_content = #{keywordContent}

SELECT keyword_sq
  FROM keyword
 WHERE keyword_content = #{keywordContent}

SELECT keyword_content
  FROM keyword
 WHERE keyword_sq = #{keywordSq}
