mapper.update:
UPDATE keyword 
   SET keyword_content = #{keywordContent},
	   keyword_st = #{keywordSt}
 WHERE keyword = #{keywordSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT keyword_sq, keyword_content, keyword_st FROM keyword WHERE 1=1
AND keyword_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY keyword_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
