mapper.select:
SELECT * 
FROM plan A inner join plan_use B on A.plan_sq = B.plan_sq
WHERE mem_id = #{memId}

SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT * FROM plan A inner join plan_use B on A.plan_sq = B.plan_sq
AND PLAN_NM LIKE '%' || #{searchKeyword} || '%'
AND MEM_ID LIKE '%' || #{searchKeyword} || '%'
AND START_DATE LIKE '%' || #{searchKeyword} || '%'
) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

SELECT ROWNUM rn, c.* FROM ( SELECT a.plan_nm as planNm, a.plan_price as planPrice, b.useCount, a.plan_price * b.useCount AS sale FROM plan a, (SELECT plan_sq, count(*) as useCount FROM plan_use
WHERE start_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND ${'sysdate'}
WHERE start_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND to_date(#{endDate},'YYYY-MM-DD')
GROUP BY plan_sq)b WHERE a.plan_sq = b.plan_sq
AND a.plan_nm LIKE '%' || #{searchKeyword} || '%'
) c
