mapper.update:
UPDATE question_gb 
   SET quest_gb_content = #{questGbContent},
	   quest_gb_st = #{questGbSt}   
 WHERE quest_gb_sq = #{questGbSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT quest_gb_sq, quest_gb_content, quest_gb_st FROM question_gb WHERE 1=1
AND quest_gb_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY quest_gb_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
