mapper.update:
UPDATE REPLY
SET    REPLY_CONTENT      = #{replyContent},
       REPLY_ST        = #{replySt}
WHERE  REPLY_SQ      = #{replySq}

mapper.select:
    SELECT A.*
      FROM (
      	SELECT ROWNUM RNUM,
           	   S.*
	      FROM (
	      	SELECT *
				FROM REPLY
				WHERE BOARD_SQ = #{boardSq}
				ORDER BY REPLY_SQ DESC
	      ) S
	      WHERE ROWNUM  <=  ${lastIndex} 
      ) A
     WHERE A.RNUM > #{firstIndex}


      	SELECT count(*) totCnt
			FROM REPLY
			WHERE BOARD_SQ = #{boardSq}
