mapper.update:
UPDATE sample_question 
   SET samp_quest_content = #{sampQuestContent},
	   samp_quest_st = #{sampQuestSt},
	   quest_gb_sq = #{questGbSq}  
 WHERE samp_quest_sq = #{sampQuestSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT samp_quest_sq, samp_quest_content, samp_quest_st, quest_gb_sq FROM sample_question WHERE 1=1
AND samp_quest_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY samp_quest_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
