mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT script_sq, script_content, script_st, script_gb_sq FROM script WHERE 1=1
AND script_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY script_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

mapper.update:
UPDATE script
   SET script_content = #{scriptContent},
 	   script_st = #{scriptSt},
 	   script_gb_sq = #{scriptGbSq}
 WHERE script_sq = #{scriptSq}
