mapper.update:
UPDATE script_gb
SET script_gb_content = #{scriptGbContent},
script_gb_st = #{scriptGbSt}
WHERE script_gb_sq = #{scriptGbSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT script_gb_sq, script_gb_content, script_gb_st FROM script_gb WHERE 1=1
AND script_gb_content LIKE '%' || #{searchKeyword} || '%'
ORDER BY script_gb_content ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
