mapper.update:
UPDATE talent 
   SET talent_nm = #{talentNm},
 	   talent_st = #{talentSt}
 WHERE talent_sq = #{talentSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT talent_sq, talent_nm, talent_st FROM talent WHERE 1=1
AND talent_nm LIKE '%' || #{searchKeyword} || '%'
ORDER BY talent_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}

SELECT ROWNUM rn, f.* FROM (SELECT e.talent_nm, count(*) as talentCount FROM talent e, keyword_analysis d ,answer c,question b, (SELECT interview_sq , interview_date FROM interview WHERE 1=1
AND interview_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND ${'sysdate'}
AND interview_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND to_date(#{endDate},'YYYY-MM-DD')
) a WHERE a.interview_sq = b.interview_sq AND b.quest_sq = c.quest_sq AND c.ans_sq = d.ans_sq AND d.talent_sq = e.talent_sq GROUP BY d.talent_sq, e.talent_nm ORDER BY d.talent_sq) f

SELECT ROWNUM rn, f.* FROM (SELECT e.talent_nm, count(*) as talentCount FROM talent e, keyword_analysis d ,answer c,question b, (SELECT interview_sq , interview_date FROM interview WHERE 1=1
AND interview_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND ${'sysdate'}
AND interview_date BETWEEN to_date(#{startDate},'YYYY-MM-DD') AND to_date(#{endDate},'YYYY-MM-DD')
) a WHERE a.interview_sq = b.interview_sq AND b.quest_sq = c.quest_sq AND c.ans_sq = d.ans_sq AND d.talent_sq = e.talent_sq
AND e.talent_nm LIKE '%' || #{searchKeyword} || '%'
GROUP BY d.talent_sq, e.talent_nm ORDER BY d.talent_sq) f
