mapper.update:
UPDATE talent 
   SET talent_nm = #{talentNm},
 	   talent_st = #{talentSt}
 WHERE talent_sq = #{talentSq}

mapper.select:
SELECT A.* FROM ( SELECT ROWNUM RNUM, S.* FROM ( SELECT talent_sq, talent_nm, talent_st FROM talent WHERE 1=1
AND talent_nm LIKE '%' || #{searchKeyword} || '%'
ORDER BY talent_sq ) S WHERE ROWNUM <= ${lastIndex} ) A WHERE A.RNUM > #{firstIndex}
